import { useState } from 'react';
import Header from '../components/Header';
import ExerciseCard from '../components/Routine/ExerciseCard';
import * as S from '../styles/Routine/Routine.style';

function Routine() {
  const exercises = [
    {
      id: 1,
      type: '유산소',
      part: '전신',
      name: '버피',
      numbers: 0,
      duration: 4,
      calories: 2.5
    },
    {
      id: 2,
      type: '유산소',
      part: '하체',
      name: '자전거 타기',
      numbers: 0,
      duration: 600,
      calories: 100
      },
    {
      id: 3,
      type: '유산소',
      part: '전신',
      name: '마운틴 클라이머',
      numbers: 0,
      duration: 1,
      calories: 1
      },
    {
      id: 4,
      type: '근력 운동',
      part: '가슴',
      name: '벤치프레스',
      numbers: 0,
      duration: 3,
      calories: 0.15
      },
    {
      id: 5,
      type: '근력 운동',
      part: '하체',
      name: '스쿼트',
      numbers: 0,
      duration: 4,
      calories: 0.25
      },
    {
      id: 6,
      type: '근력 운동',
      part: '등',
      name: '데드리프트',
      numbers: 0,
      duration: 3,
      calories: 0.2
      },
    {
      id: 7,
      type: '코어강화',
      part: '복부',
      name: '크런치',
      numbers: 0,
      duration: 0.8,
      calories: 0.25
      },
    {
      id: 8,
      type: '코어강화',
      part: '전신',
      name: '플랭크',
      numbers: 0,
      duration: 60,
      calories: 20
      },
    {
      id: 9,
      type: '코어강화',
      part: '전신',
      name: '버드독',
      numbers: 0,
      duration: 2.5,
      calories: 0.3
      },
    {
      id: 10,
      type: '유연성강화',
      part: '햄스트링',
      name: '햄스트링 스트레칭',
      numbers: 0,
      duration: 20,
      calories: 2
      },
    {
      id: 11,
      type: '유연성강화',
      part: '하체',
      name: '다리 스트레칭',
      numbers: 0,
      duration: 20,
      calories: 2
      }   ,
    {
      id: 12,
      type: '유연성강화',
      part: '어깨',
      name: '어깨 스트레칭',
      numbers: 0,
      duration: 20,
      calories: 2
      }   
  ]
  //카드 오픈 state
  const [openCard, setOpenCard] = useState(false)
  const addRoutine = () => {
    setOpenCard(!openCard)
  }
  //페이지
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(8);
  const indexOfLast = currentPage * postsPerPage;
  const indexOfFirst = indexOfLast - postsPerPage;
  const firstPage = (posts) => {
    let currentPosts = 0;
    currentPosts = posts.slice(indexOfFirst, indexOfLast);
    return currentPosts;
  };
  const secondPage = (posts) => {
    let currentPosts = 0;
    currentPosts = posts.slice(indexOfLast, posts.length)
    return currentPosts;
  }
  //페이지 보여주기
  const [page, setPage] = useState(true)
  const clickPage = () => {
    setPage(!page)
  }

  return (
    <S.Container>
      <Header route="Routine" />
      <S.RoutineBox openCard={openCard}>
        <S.AddRoutineCard openCard={openCard}>
          {openCard ? null : <S.AddIcon onClick={addRoutine} src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/57/Circled_plus.svg/800px-Circled_plus.svg.png" />}
        </S.AddRoutineCard>
        {openCard ? (
          <S.CardBox openCard={openCard}>
            {firstPage(exercises).map((exercise) => (
              <ExerciseCard
                key={exercise.id}
                id={exercise.id}
                type={exercise.type}
                part={exercise.part}
                name={exercise.name}
                duration={exercise.duration}
                calories={exercise.calories} 
                openCard={openCard} />
            ))}
            {secondPage(exercises).map((exercise) => (
              <ExerciseCard
                key={exercise.id}
                id={exercise.id}
                type={exercise.type}
                part={exercise.part}
                name={exercise.name}
                duration={exercise.duration}
                calories={exercise.calories} 
                openCard={openCard} />
            ))}
          </S.CardBox> 
          ): 
          null
        }
      </S.RoutineBox>
    </S.Container>
  )
}

export default Routine;
