import { useState, useRef } from "react";
import * as S from "../styles/Register.style";

function Register() {
  const nameRef = useRef(null);
  const ageRef = useRef(null);
  const genderRef = useRef(null);
  const heightRef = useRef(null);
  const weightRef = useRef(null);

  const [registerForm, setRegisterForm] = useState({
    name: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
  })
  const getRegisterData = (e) => {
    setRegisterForm({
      ...registerForm,
      [e.target.name]: e.target.value
    })
    console.log(inputRef.current.value)
  }
  return (
    <S.Container>
      <S.RegisterWindow>
        프로필을 등록해주세요<br />
        <div>
          이름: <S.RegisterInput ref={nameRef} name="name"/><br />
          나이: <S.RegisterInput ref={ageRef} name="age"/><br />
          성별: <S.RegisterInput ref={genderRef} name="gender"/><br />
          키: <S.RegisterInput ref={heightRef} name="height"/><br />
          몸무게: <S.RegisterInput ref={weightRef} name="weight"/><br />
        </div>
      </S.RegisterWindow>
      <button onClick={getRegisterData}>등록!</button>
    </S.Container>
  )
}

export default Register;
