import styled from "styled-components";
import '../App.css';


export const Header = styled.div`
  display: grid;
  width: 100%;
  height: 50px;
  grid-template-columns: 70% 30%;
  background-color: black;
  font-family: "OpenSansMedium";
  color: white;

`

export const Link = styled.div`
  display: flex;
  justify-content: flex-end;
  width: 100%;
  padding-top: 12px;
  color: white;
  text-decoration: none;

`

export const Menu = styled.span`
  font-family: "OpenSansLight";
  height: 30px;
  margin-left: 15px;
  color: white;
  text-decoration: none;
  &:first-child {
    border-bottom: ${props => props.route === "Logs"? "1px solid white" : "none"};
  }
  &:nth-child(2) {
    border-bottom: ${props => props.route === "Routine"? "1px solid white" : "none"};
  }
  &:nth-child(2) {
    border-bottom: ${props => props.route === "MyPage"? "1px solid white" : "none"};
  }
`
