import { useState } from 'react';
import * as S from '../../styles/Routine/CardSection.style'

function CardSection({ routineExercises, openCard }) {
  const [exerciseSet, SetExerciseSet] = useState(0);
  const addSet = () => {
    SetExerciseSet(prev => prev +1)
  }
  return (  
  <S.CardSection openCard={openCard}>
    {routineExercises.map((routineExercise) => (
      <S.AddedExercise openCard={openCard}>
        <S.CardText>{routineExercise.type}/{routineExercise.part}</S.CardText>
        <S.CardMainText>
          {routineExercise.name} <br/>
        </S.CardMainText>
        <S.CardMainText>  
          <span>세트 수: {exerciseSet}</span> <S.AddSetButton>+</S.AddSetButton><br/>
          <span> 운동 시간: </span>
        </S.CardMainText>
      </S.AddedExercise>
    ))}
  </S.CardSection>)
}

export default CardSection;
