import { useState } from 'react';
import * as S from '../../styles/Routine/CardSection.style'

function CardSection({ routineExercises, addNumbers, openCard }) {
  const [amount, setAmount] = useState(0)

  return (  
  <S.CardSection openCard={openCard}>
    {routineExercises.map((routineExercise, i) => (
      <S.AddedExercise openCard={openCard}>
        <S.CardText>{routineExercise.type}/{routineExercise.part}</S.CardText>
        <S.CardMainText>
          {routineExercise.name} <br/>
        </S.CardMainText>
        <S.CardMainText>  
          <span>세트당 횟수: {routineExercise.numbers}</span> <S.AddNumbersButton onClick={() => addNumbers(i)}>+</S.AddNumbersButton><br/>
          <span>세트 수: </span> <S.AddNumbersButton>+</S.AddNumbersButton><br/>
          <span>운동 시간: {(routineExercise.numbers*routineExercise.duration/60)}</span>
        </S.CardMainText>
      </S.AddedExercise>
    ))}
  </S.CardSection>)
}

export default CardSection;
