import { useLocation } from "react-router-dom";
import * as S from '../styles/Exercise/Exercise.style'
import { ExerciseCard } from "../styles/Routine/Routine.style";


function Exercise() {
  const location = useLocation()
  const { routine } = location.state
  console.log(routine)
  const name = routine[routine.length - 1].name;

  return (
    <S.Container>
      {routine.map((exercise, idx) => (
        <ExerciseCard 
          key={idx}
          type={exercise.type}
          part={exercise.part}
          name={exercise.name}
          numbers={exercise.numbers}
          sets={exercise.sets}
          duration={exercise.duration}
          calories={exercise.calories} 
        />
      ))}
    </S.Container>


  )
};

export default Exercise;
